angular.module("TempApp",["ngMaterial"]).controller("AppCtrl",function($scope,$mdDialog){function DialogController($scope,$mdDialog){$scope.hide=function(){$mdDialog.hide()},$scope.cancel=function(){$mdDialog.cancel()},$scope.answer=function(answer){$mdDialog.hide(answer)}}$scope.showAdvanced=function(ev){$mdDialog.show({controller:DialogController,templateUrl:"../views/dialog.tmpl.html",parent:angular.element(document.body),targetEvent:ev,clickOutsideToClose:!0,fullscreen:$scope.customFullscreen}).then(function(answer){$scope.status='You said the information was "'+answer+'".'},function(){$scope.status="You cancelled the dialog."})}});